import React from "react";
import axios from "axios";

class Home extends React.Component {
  //t_code: 택배사코드, t_invoice: 운송장 번호
  //대한통운: 04, 한진택배: 05, 경동택배: 23
  constructor(props) {
    super(props);
    this.state = {
      isClick: false,
      date: "검색해주세요.",
      t_code: "null",
      t_invoice: "null",
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  handleChange(event) {
    this.setState({
      [event.target.name]: event.target.value,
    });
  }
  handleSubmit(event) {
    this.setState({
      isClick: true,
    });
    this.getDelivery();
    event.preventDefault();
  }
  getDelivery = async () => {
    const url =
      "http://localhost:5000/delivery/find?t_code=" +
      this.state.t_code +
      "&t_invoice=" +
      this.state.t_invoice;
    const data = await axios.get(url).catch(function (error) {
      console.log(error);
    });
    this.setState({
      isClick: false,
      data: data,
    });
  };
  render() {
    const { isClick } = this.state;
    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <select name="t_code" onChange={this.handleChange}>
            <option defaultValue value="null">
              택배사 정보
            </option>
            <option value="04">CJ대한통운</option>
            <option value="05">한진택배</option>
            <option value="23">경동택배</option>
          </select>
          <input
            type="text"
            name="t_invoice"
            value={this.state.code}
            onChange={this.handleChange}
          />
          <input type="submit" value="확 인" />
        </form>
        {isClick ? <div>검색중...</div> : <div>{this.state.date}</div>}
      </div>
    );
  }
}
export default Home;
