import React from "react";
import axios from "axios";
import Delivery from "../Components/Delivery";

class Home extends React.Component {
  //t_code: 택배사코드, t_invoice: 운송장 번호
  //대한통운: 04, 한진택배: 05, 경동택배: 23
  constructor(props) {
    super(props);
    this.state = {
      isLoading: true,
      info: {},
      date: [],
      t_code: "null",
      t_invoice: "",
    };
    this.getDelivery = this.getDelivery.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  handleChange(event) {
    this.setState({
      [event.target.name]: event.target.value,
    });
  }
  handleSubmit(event) {
    if (this.state.t_code === "null" || this.state.t_invoice === "") {
      alert("입력값을 확인해주세요.");
    } else {
      this.getDelivery();
    }
    event.preventDefault();
  }
  getDelivery = async () => {
    const url =
      "http://localhost:5000/delivery/find?t_code=" +
      this.state.t_code +
      "&t_invoice=" +
      this.state.t_invoice;
    const {
      data: { message },
    } = await axios.get(url).catch(function (error) {
      console.log(error);
    });
    this.setState({
      info: message,
      data: message.trackingDetails,

      isLoading: false,
    });
    console.log(this.state.data);
  };
  render() {
    const { isLoading, data } = this.state;
    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <select name="t_code" onChange={this.handleChange}>
            <option defaultValue value="null">
              택배사 정보
            </option>
            <option value="04">CJ대한통운</option>
            <option value="05">한진택배</option>
            <option value="23">경동택배</option>
          </select>
          <input
            type="text"
            name="t_invoice"
            value={this.state.code}
            onChange={this.handleChange}
          />
          <input type="submit" value="확 인" />
        </form>
        {isLoading ? (
          <div>검색해주세요.</div>
        ) : (
          <div>
            {data.map((data, index) => (
              <Delivery key={data._id} data={data} />
            ))}
          </div>
        )}
      </div>
    );
  }
}
export default Home;
